pragma solidity ^0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./AuthenticityRegistry.sol";
import "../token/AutentiskERC721.sol";

contract Autentisk is Ownable {
    event CollectionCreated(address indexed collectionAddress);
    event AuthenticityRegistryCreated(address indexed registry);

    address public immutable AUTHENTICITY_REGISTRY;

    mapping(AutentiskERC721 => address) public s_collectionOwners;
    uint256 public s_totalCollection;

    constructor(address registry) {
        AUTHENTICITY_REGISTRY = registry;
        emit AuthenticityRegistryCreated(AUTHENTICITY_REGISTRY);
    }

    modifier onlyCollectionOwner(AutentiskERC721 collection) {
        require(
            s_collectionOwners[collection] == msg.sender,
            "Autentisk: Not collection owner"
        );
        _;
    }

    modifier onlyAuthenticityRegistry() {
        require(
            msg.sender == AUTHENTICITY_REGISTRY,
            "Autentisk: Not AuthenticityRegistry"
        );
        _;
    }

    function createCollection(string calldata name, string calldata symbol)
        external
    {
        AutentiskERC721 collection = new AutentiskERC721(name, symbol);

        s_collectionOwners[collection] = msg.sender;
        s_totalCollection++;

        emit CollectionCreated(address(collection));
    }

    function mint(
        AutentiskERC721 collection,
        address to,
        string calldata tokenURI,
        string calldata encodedTokenURI
    ) external onlyCollectionOwner(collection) {
        AuthenticityRegistry(AUTHENTICITY_REGISTRY).checkAuthenticity(
            to,
            tokenURI,
            encodedTokenURI,
            address(collection)
        );
    }

    function fulfillMint(
        AutentiskERC721 collection,
        address to,
        string calldata tokenURI
    ) external onlyAuthenticityRegistry {
        collection.mint(to, tokenURI);
    }
}
","keccak256":"0xadcdb0503075c2df34d75c9270d3c34a00aa65b5e0a7db6c6c2b66ea34b5f920","license":"MIT"},"contracts/marketplace/AuthenticityRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {Chainlink, ChainlinkClient} from "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC20} from "@openzeppelin/contracts/interfaces/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {AutentiskERC721} from "../token/AutentiskERC721.sol";
import {Autentisk} from "./Autentisk.sol";

contract AuthenticityRegistry is ChainlinkClient, Ownable {
    using Chainlink for Chainlink.Request;

    struct AuthenticityRequest {
        address to;
        string tokenURI;
        address collection;
    }

    event OracleChanged(address prevOracle, address newOracle, bytes32 jobId);
    event ClassifierUrlChanged(string value);
    event AuthenticityRequested(
        bytes32 uriSignature,
        address collection,
        bytes32 requestId
    );
    event AuthenticityFulfilled(
        bytes32 requestId,
        uint256 similarity,
        bool isAccepted
    );

    address public immutable AUTENTISK;

    mapping(bytes32 => address) public s_autentics;
    mapping(bytes32 => AuthenticityRequest) public s_authenticityRequests;
    mapping(bytes32 => bytes32) public s_signatureToRequestId;

    string public s_classifierUrl;
    uint256 public s_similarityThreshold;

    address public s_oracle;
    bytes32 public s_jobId;
    uint256 public s_fee;

    constructor(
        address controller,
        address autentisk,
        address oracle,
        string memory jobId,
        uint256 fee,
        string memory classifierUrl,
        uint256 similarityThreshold,
        address link
    ) {
        _transferOwnership(controller);
        AUTENTISK = autentisk;
        setChainlinkToken(link);
        setOracle(oracle, bytes32(bytes(jobId)), fee);
        setClassifierUrl(classifierUrl);
        s_similarityThreshold = similarityThreshold;
    }

    modifier onlyAutentisk() {
        require(msg.sender == AUTENTISK, "AuthenticityRegistry: Not Autentisk");
        _;
    }

    function withdrawToken(IERC20 token) external {
        SafeERC20.safeTransfer(
            token,
            Autentisk(AUTENTISK).owner(),
            IERC20(token).balanceOf(address(this))
        );
    }

    function checkAuthenticity(
        address to,
        string calldata tokenURI,
        string calldata encodedTokenURI,
        address collection
    ) external onlyAutentisk returns (bytes32 requestId_) {
        bytes32 uriSignature = keccak256(abi.encodePacked(tokenURI));
        require(
            s_autentics[uriSignature] == address(0),
            "TokenURI has been registered"
        );
        require(bytes(tokenURI).length > 0, "Token URI can't be empty");

        Chainlink.Request memory request = buildChainlinkRequest(
            s_jobId,
            address(this),
            this.fulfillAuthenticity.selector
        );

        request.add("get", makeRequestUrl(encodedTokenURI));
        request.add("path", "similarity");

        requestId_ = sendChainlinkRequestTo(s_oracle, request, s_fee);
        s_authenticityRequests[requestId_] = AuthenticityRequest(
            to,
            tokenURI,
            collection
        );
        s_signatureToRequestId[uriSignature] = requestId_;

        emit AuthenticityRequested(uriSignature, collection, requestId_);
    }

    function fulfillAuthenticity(bytes32 requestId, uint256 similarity)
        public
        recordChainlinkFulfillment(requestId)
    {
        AuthenticityRequest memory request = s_authenticityRequests[requestId];
        delete s_authenticityRequests[requestId];

        bytes32 uriSignature = keccak256(abi.encodePacked(request.tokenURI));
        delete s_signatureToRequestId[uriSignature];

        if (isSimilar(similarity)) {
            emit AuthenticityFulfilled(requestId, similarity, false);
            return;
        }

        s_autentics[uriSignature] = request.collection;
        emit AuthenticityFulfilled(requestId, similarity, true);

        Autentisk(AUTENTISK).fulfillMint(
            AutentiskERC721(request.collection),
            request.to,
            request.tokenURI
        );
    }

    function setOracle(
        address _oracle,
        bytes32 _jobId,
        uint256 _fee
    ) public onlyOwner {
        require(_oracle != address(0), "Invalid oracle address");

        address prevOracle = s_oracle;

        s_oracle = _oracle;
        s_jobId = _jobId;
        s_fee = _fee;

        emit OracleChanged(prevOracle, _oracle, _jobId);
    }

    function setClassifierUrl(string memory value) public onlyOwner {
        s_classifierUrl = value;
        emit ClassifierUrlChanged(value);
    }

    function isSimilar(uint256 similarity) private view returns (bool) {
        return similarity < s_similarityThreshold;
    }

    function makeRequestUrl(string memory tokenUri)
        private
        view
        returns (string memory url)
    {
        return string.concat(s_classifierUrl, "?tokenUri=", tokenUri);
    }
}
","keccak256":"0x69922305586dbf5b0e54aeb93e40d9b65ce28042653d137edb9d06bdafbcde64","license":"MIT"},"contracts/token/AutentiskERC721.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {ERC721, ERC721URIStorage} from "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import {Counters} from "@openzeppelin/contracts/utils/Counters.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract AutentiskERC721 is ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    constructor(string memory name, string memory symbol)
        ERC721(name, symbol)
    {}

    function mint(address to, string calldata tokenURI)
        external
        onlyOwner
        returns (uint256 id)
    {
        _tokenIds.increment();

        id = _tokenIds.current();
        _mint(to, id);
        _setTokenURI(id, tokenURI);

        return id;
    }
}
